import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
} from 'reactstrap';


import logo from './white_logo.svg';
import Switch from './Switch';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.routeTo = this.routeTo.bind(this);
    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: false
    };
  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }

  routeTo(type, suffix) {
    const { router } = this.props;
    if (type === 'routeback') {
      window.location = router.previousRoute ? `#${router.previousRoute.path}` : '#';
    } else if (type === 'routeahead') {
      window.location = `#${router.route.path}/${suffix}`;
    } else {
      window.location = `#/${suffix}`;
    }
  }


  render() {
    const { router, moduleMappings } = this.props;
    const nonHomeRoutes = moduleMappings;
    delete nonHomeRoutes['home'];
    return (
      <div>
        <Navbar className="propNav" light expand="md" fixed="top">
          <NavbarBrand href="/">
            <div>
              <img style={{ height: "50px" }} src={logo} />
            </div>
          </NavbarBrand>
          <NavbarToggler onClick={this.toggle} />
          <Collapse isOpen={this.state.isOpen} navbar>
            <Nav className="ml-auto" navbar>
              {Object.keys(nonHomeRoutes).map(route => (
                <NavItem >
                  <NavLink style={{ color: "white" }} href={route.path}>{route.label}</NavLink>
                </NavItem>
              ))}
            </Nav>
          </Collapse>
        </Navbar>

        <Switch moduleMappings={moduleMappings} routeTo={this.routeTo} />
      </div>
    );
  }
}

App.propTypes = {
  moduleMappings: PropTypes.objectOf(PropTypes.object).isRequired,
  router: PropTypes.objectOf(PropTypes.object).isRequired,
  isLoggedIn: PropTypes.func.isRequired,
  businessId: PropTypes.string.isRequired,
  businessName: PropTypes.string.isRequired,
};

const mapStateToProps = state => ({
  router: state.router,
});

export default connect(mapStateToProps)(App);
